import React, { useState } from 'react';
import { 
  FaPalette, 
  FaRuler, 
  FaHeading, 
  FaImage, 
  FaFont, 
  FaTag, 
  FaSquare, 
  FaAlignLeft 
} from 'react-icons/fa';
import './DraggableItem.css';

const DraggableItem = ({ id, label, type, required }) => {
  const [showColorInput, setShowColorInput] = useState(false);
  const [colorValue, setColorValue] = useState('#ffffff');

  const getIcon = () => {
    switch (id) {
      case 'background':
        return <FaPalette />;
      case 'formWidth':
        return <FaRuler />;
      case 'heading':
        return <FaHeading />;
      case 'logo':
        return <FaImage />;
      case 'headingColor':
        return <FaFont />;
      case 'inputLabel':
        return <FaTag />;
      case 'buttonColor':
        return <FaSquare />;
      case 'buttonText':
        return <FaAlignLeft />;
      default:
        return null;
    }
  };

  const handleClick = () => {
    if (id === 'background') {
      setShowColorInput(!showColorInput);
    }
  };

  const handleColorChange = (e) => {
    setColorValue(e.target.value);
  };

  return (
    <div className="form-control-item">
      <div className="item-content" onClick={handleClick}>
        <div className="item-icon">
          {getIcon()}
        </div>
        <span className="item-label">
          {label}
          {required && <span className="required-indicator">*</span>}
        </span>
        <div className="item-type">
          {type}
        </div>
      </div>
      {id === 'background' && showColorInput && (
        <div className="color-input-container">
          <input
            type="text"
            value={colorValue}
            onChange={handleColorChange}
            placeholder="Enter color code"
            className="color-text-input"
          />
          <input
            type="color"
            value={colorValue}
            onChange={handleColorChange}
            className="color-picker-input"
          />
        </div>
      )}
    </div>
  );
};

export default DraggableItem;
