import React from 'react';
import { useDrop } from 'react-dnd';
import { FaTrash, FaGripVertical, FaEnvelope, FaSms, FaBell } from 'react-icons/fa';
import './DropZone.css';

const DropZone = ({ 
  onDrop, 
  items, 
  onDelete, 
  onReorder, 
  isPreviewMode,
  formValues,
  formErrors,
  onValueChange 
}) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'formItem',
    drop: (item) => onDrop(item),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  const handleDragEnd = (result) => {
    if (!result.destination) return;
    onReorder(result.source.index, result.destination.index);
  };

  const renderFormControl = (item) => {
    const value = formValues[item.id] || '';
    const error = formErrors[item.id];

    const handleChange = (e) => {
      const newValue = e.target.type === 'checkbox' 
        ? e.target.checked
        : e.target.type === 'file' 
          ? e.target.files[0]?.name || '' 
          : e.target.value;
      onValueChange(item.id, newValue);
    };

    if (item.type === 'preference') {
      return (
        <div className="preference-control">
          <div className="preference-header">
            <div className="preference-icon">
              {item.preferenceType === 'email' && <FaEnvelope />}
              {item.preferenceType === 'sms' && <FaSms />}
              {item.preferenceType === 'push' && <FaBell />}
            </div>
            <label htmlFor={item.id}>{item.label}</label>
          </div>
          <div className="preference-toggle">
            <input
              type="checkbox"
              id={item.id}
              checked={value || false}
              onChange={handleChange}
              disabled={isPreviewMode}
            />
            <span className="toggle-slider"></span>
          </div>
        </div>
      );
    }

    const commonProps = {
      id: item.id,
      value: value,
      onChange: handleChange,
      className: `${item.type}-input ${error ? 'has-error' : ''}`,
      disabled: isPreviewMode
    };

    switch (item.type) {
      case 'color':
        return (
          <div className="form-control">
            <label htmlFor={item.id}>
              {item.label}
              {item.required && <span className="required">*</span>}
            </label>
            <input 
              type="color"
              {...commonProps}
            />
            {error && <span className="error-message">{error}</span>}
          </div>
        );
      case 'select':
        return (
          <div className="form-control">
            <label htmlFor={item.id}>
              {item.label}
              {item.required && <span className="required">*</span>}
            </label>
            <select {...commonProps}>
              <option value="">Select width...</option>
              <option value="400px">400px</option>
              <option value="600px">600px</option>
              <option value="800px">800px</option>
            </select>
            {error && <span className="error-message">{error}</span>}
          </div>
        );
      case 'file':
        return (
          <div className="form-control">
            <label htmlFor={item.id}>
              {item.label}
              {item.required && <span className="required">*</span>}
            </label>
            <input 
              type="file"
              onChange={handleChange}
              className={`file-input ${error ? 'has-error' : ''}`}
              disabled={isPreviewMode}
            />
            {error && <span className="error-message">{error}</span>}
          </div>
        );
      case 'text':
        return (
          <div className="form-control">
            <label htmlFor={item.id}>
              {item.label}
              {item.required && <span className="required">*</span>}
            </label>
            <input 
              type="text"
              {...commonProps}
              placeholder={`Enter ${item.label.toLowerCase()}`}
            />
            {error && <span className="error-message">{error}</span>}
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div 
      ref={drop} 
      className={`drop-zone ${isOver ? 'is-over' : ''}`}
    >
      {items.length === 0 ? (
        <div className="drop-placeholder">
          <p>Drag and drop form controls here</p>
        </div>
      ) : (
        <div className="form-preview-container">
          {items.map((item, index) => (
            <div
              key={item.id}
              className="preview-item"
            >
              {!isPreviewMode && (
                <div className="item-actions">
                  <div className="drag-handle">
                    <FaGripVertical />
                  </div>
                  <button 
                    className="delete-button"
                    onClick={() => onDelete(item.id)}
                    title="Delete item"
                  >
                    <FaTrash />
                  </button>
                </div>
              )}
              {renderFormControl(item)}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default DropZone;
