import React from 'react';
import { useDrag } from 'react-dnd';
import { 
  FaPalette, 
  FaRuler, 
  FaHeading, 
  FaImage, 
  FaFont, 
  FaTag, 
  FaSquare, 
  FaAlignLeft 
} from 'react-icons/fa';
import './DraggableItem.css';

const DraggableItem = ({ id, label, type, required }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: 'formItem',
    item: { id, label, type, required },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  const getIcon = () => {
    switch (id) {
      case 'background':
        return <FaPalette />;
      case 'formWidth':
        return <FaRuler />;
      case 'heading':
        return <FaHeading />;
      case 'logo':
        return <FaImage />;
      case 'headingColor':
        return <FaFont />;
      case 'inputLabel':
        return <FaTag />;
      case 'buttonColor':
        return <FaSquare />;
      case 'buttonText':
        return <FaAlignLeft />;
      default:
        return null;
    }
  };

  const getTooltipText = () => {
    switch (id) {
      case 'background':
        return 'Choose the form background color';
      case 'formWidth':
        return 'Set the width of the form';
      case 'heading':
        return 'Add a heading to your form';
      case 'logo':
        return 'Upload your company logo';
      case 'headingColor':
        return 'Set the heading text color';
      case 'inputLabel':
        return 'Choose the color for input labels';
      case 'buttonColor':
        return 'Set the submit button color';
      case 'buttonText':
        return 'Customize the submit button text';
      default:
        return '';
    }
  };

  return (
    <div
      ref={drag}
      className={`draggable-item ${isDragging ? 'dragging' : ''}`}
      title={getTooltipText()}
    >
      <div className="item-content">
        <div className="item-icon">
          {getIcon()}
        </div>
        <span className="item-label">
          {label}
          {required && <span className="required-indicator">*</span>}
        </span>
      </div>
      <div className="item-type">
        {type}
      </div>
    </div>
  );
};

export default DraggableItem;
